function tfCalculator(){
    var output = [];
    for (var i=0; i < Corpus.length; i++) {
        var word = Corpus[i];
        if (output[word] === undefined) {
            output[word] = 1;
        }   
        else {
            output[word] += 1;
        }
    
    }
    for(var i = 0; i<index.length; i++){
        if(output[index[i]] != undefined )
            indextf[docCounter][i] = output[index[i]];
        else
            indextf[docCounter][i] = 0;
    }
}

function idfCalculator(){
    for(var i=0; i<index.length; i++ )
        if(Corpus.includes(index[i]))
            idf[i]++;            
}

function addtoIndex(){
    let unique;
    for(var i=0; i<Corpus.length; i++ ){
        unique = index.join(" ");
        if(unique.includes(Corpus[i]) == false)
            index  = index.concat(Corpus[i]);
    }
}

function weightCalculator(){
    var largest= [];
    for(var i=0; i<docCounter; i++){
        largest[i] = 0;
        for (var j=0; j<indexCounter[i]; j++){
            if (indextf[i][j]>largest[i]) 
                largest[i] = indextf[i][j];
        }
    }
    for(var i=0; i<=docCounter; i++){
        for(var j=0; j<indexCounter[i]; j++){
            
            weight[i][j] = (indextf[i][j]/largest[i]) * (Math.log(docCounter)/Math.log(2) - Math.log(idf[j])/Math.log(2));
        }
    }
    console.log(weight[1][1]);
}

